import{_ as s,o as n,c as a,O as l}from"./chunks/framework.4afe7240.js";const i=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"【通用】编码 ASCII，Unicode 和 UTF-8.md","filePath":"【通用】编码 ASCII，Unicode 和 UTF-8.md"}'),p={name:"【通用】编码 ASCII，Unicode 和 UTF-8.md"},o=l(`<div class="language-javascript"><button title="Copy Code" class="copy"></button><span class="lang">javascript</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">一、ASCII 码</span></span>
<span class="line"><span style="color:#A6ACCD;">    8</span><span style="color:#82AAFF;">个二进制位</span><span style="color:#A6ACCD;">(从00000000到11111111)就可以组合出256种状态，这被称为一个字节（byte）</span></span>
<span class="line"><span style="color:#A6ACCD;">    ASCII 码一共规定了128个字符的编码，比如空格SPACE是32（二进制00100000），大写的字母A是65（二进制01000001）</span></span>
<span class="line"><span style="color:#A6ACCD;">        这128个符号（包括32个不能打印出来的控制符号），只占用了一个字节的后面7位，最前面的一位统一规定为0。</span></span>
<span class="line"><span style="color:#A6ACCD;">二、非 ASCII 编码</span></span>
<span class="line"><span style="color:#A6ACCD;">    其他语言，128个符号是不够的。一些欧洲国家就决定，利用字节中闲置的最高位编入新的符号。</span></span>
<span class="line"><span style="color:#A6ACCD;">        比如，法语中的é的编码为130（二进制10000010）。这样一来，这些欧洲国家使用的编码体系，可以表示最多256个符号。</span></span>
<span class="line"><span style="color:#A6ACCD;">    每个国家编码方式中，</span><span style="color:#F78C6C;">0</span><span style="color:#89DDFF;">--</span><span style="color:#A6ACCD;">127表示的符号是一样的，不一样的只是128</span><span style="color:#89DDFF;">--</span><span style="color:#A6ACCD;">255的这一段。</span></span>
<span class="line"><span style="color:#A6ACCD;">    简体中文常见的编码方式是 GB2312，使用两个字节表示一个汉字，所以理论上最多可以表示 </span><span style="color:#F78C6C;">256</span><span style="color:#A6ACCD;"> x </span><span style="color:#F78C6C;">256</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">65536</span><span style="color:#A6ACCD;"> 个符号。</span></span>
<span class="line"><span style="color:#A6ACCD;">        虽然都是用多个字节表示一个符号，但是GB类的汉字编码与后文的 Unicode 和 UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 是毫无关系的。</span></span>
<span class="line"><span style="color:#A6ACCD;">        </span></span>
<span class="line"><span style="color:#A6ACCD;">三</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;"> Unicode是一种所有符号的编码</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">可容纳100多万个符号，比如，U</span><span style="color:#89DDFF;">+</span><span style="color:#A6ACCD;">4E25表示汉字严。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">四、Unicode 的问题</span></span>
<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#676E95;font-style:italic;">//只规定了符号的二进制代码，没有规定这个二进制代码应该如何存储</span></span>
<span class="line"><span style="color:#A6ACCD;">    比如，汉字严的 Unicode 是十六进制数4E25，转换成二进制数足足有15位（</span><span style="color:#F78C6C;">100111000100101</span><span style="color:#A6ACCD;">），也就是说，这个符号的表示至少需要2个字节。</span></span>
<span class="line"><span style="color:#A6ACCD;">    表示其他更大的符号，可能需要3个字节或者4个字节，甚至更多。</span></span>
<span class="line"><span style="color:#A6ACCD;">    这里就有两个严重的问题</span></span>
<span class="line"><span style="color:#A6ACCD;">        第一个问题是，如何才能区别 Unicode 和 ASCII ？计算机怎么知道三个字节表示一个符号，而不是分别表示三个符号呢？</span></span>
<span class="line"><span style="color:#A6ACCD;">        第二个问题是，我们已经知道，英文字母只用一个字节表示就够了，如果 Unicode 统一规定，每个符号用三个或四个字节表示，</span></span>
<span class="line"><span style="color:#A6ACCD;">          那么每个英文字母前都必然有二到三个字节是0，这对于存储来说是极大的浪费，文本文件的大小会因此大出二三倍，这是无法接受的。</span></span>
<span class="line"><span style="color:#A6ACCD;">    它们造成的结果是：</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">）出现了 Unicode 的多种存储方式，也就是说有许多种不同的二进制格式，可以用来表示 Unicode。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;">）Unicode 在很长一段时间内无法推广，直到互联网的出现。</span></span>
<span class="line"><span style="color:#A6ACCD;">五、</span><span style="color:#89DDFF;">,</span><span style="color:#89DDFF;">/</span><span style="color:#C3E88D;">UTF-8是Unicode 的实现方式</span><span style="color:#89DDFF;">/</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#FFCB6B;">其他实现方式</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">            UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">16</span><span style="color:#A6ACCD;">（字符用两个字节）</span></span>
<span class="line"><span style="color:#A6ACCD;">            UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">32</span><span style="color:#A6ACCD;">（字符用四个字节）</span><span style="color:#676E95;font-style:italic;">//不过在互联网上基本不用。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">/</span><span style="color:#C3E88D;">UTF-16长度固定，UTF-8长度不固定</span><span style="color:#89DDFF;">/</span></span>
<span class="line"><span style="color:#A6ACCD;">    UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 最大的一个特点，就是它是一种</span><span style="color:#89DDFF;">,</span><span style="color:#89DDFF;">/</span><span style="color:#C3E88D;">变长</span><span style="color:#89DDFF;">/</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;">的编码方式。它可以使用1</span><span style="color:#89DDFF;">~</span><span style="color:#A6ACCD;">4个字节表示一个符号，根据不同的符号而变化字节长度。</span></span>
<span class="line"><span style="color:#A6ACCD;">    UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 的编码规则很简单，只有二条：</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">）对于单字节的符号，字节的第一位设为0，后面7位为这个符号的 Unicode 码。因此对于英语字母，UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 编码和 ASCII 码是相同的。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;">）对于n字节的符号（n </span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">），第一个字节的前n位都设为1，第n </span><span style="color:#89DDFF;">+</span><span style="color:#A6ACCD;"> 1位设为0，后面字节的前两位一律设为10。剩下的没有提及的二进制位，全部为这个符号的 Unicode 码。</span></span>
<span class="line"><span style="color:#A6ACCD;">    下表总结了编码规则，字母x表示可用编码的位。</span></span>
<span class="line"><span style="color:#A6ACCD;">    Unicode符号范围     </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;">        UTF</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">8编码方式</span></span>
<span class="line"><span style="color:#A6ACCD;">    (十六进制)        </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;">              （二进制）</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">----------------------+---------------------------------------------</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0000</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> 007F </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> 0xxxxxxx</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0080</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> 07FF </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> 110xxxxx 10xxxxxx</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0800</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> FFFF </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> 1110xxxx 10xxxxxx 10xxxxxx</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">0001</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0000</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">0010</span><span style="color:#A6ACCD;"> FFFF </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> 11110xxx 10xxxxxx 10xxxxxx 10xxxxxx</span></span>
<span class="line"><span style="color:#A6ACCD;">    跟据上表，解读 UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 编码非常简单。如果一个字节的第一位是0，则这个字节单独就是一个字符；如果第一位是1，则连续有多少个1，就表示当前字符占用多少个字节。</span></span>
<span class="line"><span style="color:#A6ACCD;">    下面，还是以汉字严为例，演示如何实现 UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 编码。</span></span>
<span class="line"><span style="color:#A6ACCD;">    严的 Unicode 是4E25（</span><span style="color:#F78C6C;">100111000100101</span><span style="color:#A6ACCD;">），根据上表，可以发现4E25处在第三行的范围内（</span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0800</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0000</span><span style="color:#A6ACCD;"> FFFF），因此严的 UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 编码需要三个字节，即格式是1110xxxx 10xxxxxx 10xxxxxx。然后，从严的最后一个二进制位开始，依次从后向前填入格式中的x，多出的位补0。这样就得到了，严的 UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 编码是11100100 </span><span style="color:#F78C6C;">10111000</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">10100101</span><span style="color:#A6ACCD;">，转换成十六进制就是E4B8A5。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span></span>
<span class="line"><span style="color:#A6ACCD;">六、Unicode 与 UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 之间的转换</span></span>
<span class="line"><span style="color:#A6ACCD;">    通过上一节的例子，可以看到严的 Unicode码 是4E25，UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;"> 编码是E4B8A5，两者是不一样的。它们之间的转换可以通过程序实现。</span></span>
<span class="line"><span style="color:#A6ACCD;">    Windows平台，有一个最简单的转化方法，就是使用内置的记事本小程序notepad</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">exe。打开文件后，点击文件菜单中的另存为命令，会跳出一个对话框，在最底部有一个编码的下拉条。</span></span>
<span class="line"><span style="color:#A6ACCD;">    里面有四个选项：ANSI，Unicode，Unicode big endian和UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;">。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">）ANSI是默认的编码方式。对于英文文件是ASCII编码，对于简体中文文件是GB2312编码（只针对 Windows 简体中文版，如果是繁体中文版会采用 Big5 码）。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;">）Unicode编码这里指的是notepad</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">exe使用的 UCS</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> 编码方式，即直接用两个字节存入字符的 Unicode 码，这个选项用的 little endian 格式。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;">）Unicode big endian编码与上一个选项相对应。我在下一节会解释 little endian 和 big endian 的涵义。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">4</span><span style="color:#A6ACCD;">）UTF</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">8编码，也就是上一节谈到的编码方法。</span></span>
<span class="line"><span style="color:#A6ACCD;">    选择完</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">编码方式</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">后，点击</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">保存</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">按钮，文件的编码方式就立刻转换好了。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span></span>
<span class="line"><span style="color:#A6ACCD;">   </span></span>
<span class="line"><span style="color:#A6ACCD;">八、实例</span></span>
<span class="line"><span style="color:#A6ACCD;">    下面，举一个实例。</span></span>
<span class="line"><span style="color:#A6ACCD;">    打开</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">记事本</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">程序notepad</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">exe，新建一个文本文件，内容就是一个严字，依次采用ANSI，Unicode，Unicode big endian和UTF</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">8编码方式保存。</span></span>
<span class="line"><span style="color:#A6ACCD;">    然后，用文本编辑软件UltraEdit 中的</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">十六进制功能</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">，观察该文件的内部编码方式。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">）ANSI：文件的编码就是两个字节D1 CF，这正是严的 GB2312 编码，这也暗示 GB2312 是采用大头方式存储的。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;">）Unicode：编码是四个字节FF FE </span><span style="color:#F78C6C;">25</span><span style="color:#A6ACCD;"> 4E，其中FF FE表明是小头方式存储，真正的编码是4E25。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;">）Unicode big endian：编码是四个字节FE FF 4E </span><span style="color:#F78C6C;">25</span><span style="color:#A6ACCD;">，其中FE FF表明是大头方式存储。</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F78C6C;">4</span><span style="color:#A6ACCD;">）UTF</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">8</span><span style="color:#A6ACCD;">：编码是六个字节EF BB BF E4 B8 A5，前三个字节EF BB BF表示这是UTF</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">8编码，后三个E4B8A5就是严的具体编码，它的存储顺序与编码顺序是一致的。</span></span></code></pre></div>`,1),e=[o];function C(c,t,A,r,D,y){return n(),a("div",null,e)}const x=s(p,[["render",C]]);export{i as __pageData,x as default};
